import TinderCard from "react-tinder-card";
import { useEffect, useState } from "react";
import axios from 'axios'
import { useCookies } from "react-cookie";
import ChatContainer from "../components/ChatContainer"
const Dashboard = () => {

    const [user, setUser] = useState(null)
    const [genderedUser, setenderedUser] = useState(null)
    const [cookies, setCookies, removeCookies] = useCookies(['user'])

    const userId = cookies.UserId
    const getUser = async () => {
        try {
            const response = await axios.get('http://localhost:8000/user', {
                params: { userId }
            })
            setUser(response.data)

        } catch (err) {
            console.log(err)
        }
    }

    const getGenderedUser = () => {
        try {
            const response = axios.get('http://localhost:8000/gendered-user', {
                params: { gender: user?.gender_interest }
            })
            setGenderedUser(response.data)
        }
        catch (err) {
            console.log(err)
        }
    }

    useEffect(() => {
        getUser()
    }, [])
    console.log(user)


  const characters = [
    {
      name: "Richard Hendricks",
      url: "https://flxt.tmsimg.com/assets/487052_v9_bc.jpg",
    },
    {
      name: "Erlich Bachman",
      url: "https://flxt.tmsimg.com/assets/487052_v9_bc.jpg",
    },
    {
      name: "Monica Hall",
      url: "https://flxt.tmsimg.com/assets/487052_v9_bc.jpg",
    },
    {
      name: "Jared Dunn",
      url: "https://flxt.tmsimg.com/assets/487052_v9_bc.jpg",
    },
    {
      name: "Dinesh Chugtai",
      url: "https://flxt.tmsimg.com/assets/487052_v9_bc.jpg",
    },
  ];
  const [lastDirection, setLastDirection] = useState();
  const swiped = (direction, nameToDelete) => {
    console.log("removing: " + nameToDelete);
    setLastDirection(direction);
  };

  const outOfFrame = (name) => {
    console.log(name + " left the screen!");
  };
  return (
    <>
    {user && 
    <div className="dashboard">
      <ChatContainer user={user}/>
      <div className="swipe-container">
        <div className="card-container">
          {characters.map((character) => (
            <TinderCard
              className="swipe"
              key={character.name}
              onSwipe={(dir) => swiped(dir, character.name)}
              onCardLeftScreen={() => outOfFrame(character.name)}
            >
              <div
                style={{ backgroundImage: "url(" + character.url + ")" }}
                className="card"
              >
                <h3>{character.name}</h3>
              </div>
            </TinderCard>
          ))}
          <div className="swipe-info">
            {lastDirection ? <p>You swiped {lastDirection}</p> : <p/>}
          </div>
        </div>
      </div>
    </div>}
    </>
  );
};

export default Dashboard;
